import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, TouchableOpacity } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useUserContext } from "../src/UserContext"


const FriendCard = (props) => {
    return (
        <Card style={styles.shadow} onPress={props.onPress}>
            <View style={styles.user}>
                {/*  <Card.Cover source={{ uri: props.img }} style={{ borderRadius: 15, borderTopRightRadius: 15, borderTopLeftRadius: 15, height: 150, width: 330, }} /> */}
                <Avatar.Image size={48} source={{ uri: props.friendData.img }} />
                <Card.Title title={props.friendData.firstName + ' ' + props.friendData.lastName} subtitle={'Approximately ' + props.friendData.distance + 'km from you'} />
                <TouchableOpacity style={styles.button, styles.followButton} id="btn"><Text>Follow</Text></TouchableOpacity>
                {/* <TouchableOpacity style={styles.button, styles.unfollowButton} id="btn"><Text>Unfollow</Text></TouchableOpacity> */}
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        flex: 1,
        justifyContent: 'center',
        alignContent: 'center',
        /*  height: 300,
         width: 370, */
        /* marginTop: 8,
        marginLeft: 10,
        marginRight: 10, */
        marginBottom: 8,
        /* paddingTop: 15,
        paddingLeft: 5, */
        alignItems: 'baseline'
    },
    user: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
        marginTop: 8,
        marginLeft: 8,
    },
    button: {
        color: '#ffffff',
        elevation: 8,
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
    },
    followButton: {
        backgroundColor: '#1f9c3a'
    },
    unfollowButton: {
        backgroundColor: '#1f9c3a'
    }
    
})

export default FriendCard;
