                return response.json();
            })
            .then(async (data) => {
                user = {
                    email: data.Email,
                    firstName: data.FirstName,
                    lastName: data.LastName,
                    city: data.City,
                    distance: Math.floor(getDistanceFromLatLonInKm(currentUser.lat, currentUser.lon, data.Lat, data.Lon)),
                    img: data.Img
