import React, { useEffect } from 'react'
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Button from '../components/Button'
import { Text, Card } from 'react-native-paper'
import { SimpleLineIcons, AntDesign, Ionicons, Feather, MaterialCommunityIcons } from '@expo/vector-icons';
import Paragraph from '../components/Paragraph'
import BackButton from '../components/BackButton'
import DishCard from '../components/DishCard'
import { useUserContext } from "../src/UserContext"
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { TextInput } from 'react-native-gesture-handler'




const DishesScreen = ({ navigation }) => {
    const { currentChefDishes, currentChef, cart, setOrder } = useUserContext();
    const Tab = createMaterialBottomTabNavigator();



    function ShowDishes() {
        return (
            <View>
                <ScrollView style={{ width: '100%' }}>
                    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                        <BackButton goBack={navigation.goBack} />
                    </View>

                    <View style={{ marginTop: "15%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                        {currentChefDishes.map((item, key) =>
                            <DishCard name={item.DisplayName} img={item.Img} rating={item.Rating}
                                dishType={item.DishType} description={item.Description} price={item.Price} key={key}>
                            </DishCard>
                        )}
                    </View>
                </ScrollView >
            </View>
        );
    }

    function ShowCart() {
        return (
            <View style={{ alignItems: 'center' }}>
                <Text style={{ marginTop: '10%', fontSize: 35, textAlign: 'center', color: 'green' }}>My Cart</Text>
                {console.log(cart)}
                {(cart.length > 0) ?
                    cart.map((item, key) =>
                    (item.Quantity == 0) ? null :
                        <Card style={styles.shadow} key={key}>
                            <Card.Content >
                                <View style={{ marginTop: -10, marginBottom: -30 }}>
                                    <MaterialCommunityIcons name="silverware-fork-knife" size={24} color="black" >
                                        <Text style={{ fontSize: 16, color: 'black' }}> {item.Name}</Text>
                                        <Text style={{ fontSize: 16, color: '#8c8c8c' }}> - </Text>
                                        <Text style={{ fontSize: 16, color: 'black' }}> x {item.Quantity} {"\n"}</Text>
                                    </MaterialCommunityIcons>
                                </View>
                                <Text style={{ marginTop: 0, textAlign: "right" }}>
                                    <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 17, color: 'green' }}> {item.Price * item.Quantity}</Text>
                                </Text>
                            </Card.Content>
                        </Card>)
                    :
                    <Text style={{ textAlign: 'center' }}>Your cart is empty</Text>
                }
                {(cart.length > 0) ?
                    <Button mode="outlined" style={{ backgroundColor: 'rgb(255,138,87)' }} onPress={setOrder}>
                        <Text style={{ color: 'rgb(255 ,255, 255)', fontWeight: 'bold' }}>Order Now</Text>
                    </Button> : null
                }
            </View>
        );
    }



    return (
        <Tab.Navigator
            initialRouteName="ShowDishes"
            activeColor="#fff"
            barStyle={{ backgroundColor: 'rgb(62,197,0)' }}
        >
            <Tab.Screen name="ShowDishes" component={ShowDishes}
                options={{
                    tabBarLabel: 'Menu',
                    tabBarIcon: ({ color }) => (
                        <SimpleLineIcons name="book-open" size={20} color={color} />
                    ),
                }}
            />
            <Tab.Screen name="ShowCart" component={ShowCart}
                options={{
                    tabBarLabel: 'Cart',
                    tabBarIcon: ({ color }) => (
                        <AntDesign
                            style={styles.tabIcon}
                            name="shoppingcart"
                            size={20}
                            color={color}
                        />
                    ),
                }}
            />
        </Tab.Navigator>
    )
}

