import { Image, StyleSheet, Text, TouchableOpacity, View, Vibration } from 'react-native'
import { Avatar, Card, Title, Paragraph, IconButton, Menu, Divider, Provider, Button } from 'react-native-paper';
import * as ImagePicker from 'expo-image-picker';
import { AntDesign, MaterialCommunityIcons } from '@expo/vector-icons';
import { Camera } from 'expo-camera';
import React, { useState, useEffect } from 'react';
import { useUserContext } from "../src/UserContext"

export default function App() {
    const [hasPermission, setHasPermission] = useState(null);
    const [type, setType] = useState(Camera.Constants.Type.back);
    const [openCamera, setopenCamera] = React.useState(null);
    const { updateImage } = useUserContext();
    



    let openImagePickerAsync = async () => {
        let permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();

        if (permissionResult.granted === false) {
            alert("Permission to access camera roll is required!");
            return;
        }

        let pickerResult = await ImagePicker.launchImageLibraryAsync();
        console.log(pickerResult);

        if (pickerResult.cancelled === true) {
            return;
        }

        updateImage(photo.uri);
        /* setSelectedImage({ localUri: pickerResult.uri }); */
    }


    useEffect(() => {
        (async () => {
            const { status } = await Camera.requestPermissionsAsync();
            setHasPermission(status === 'granted');
        })();
    }, []);

    let startCamera = () => {
        setopenCamera({ uri: "1" });
    }

    const takePicture = async () => {
        Vibration.vibrate();
        if (this.camera) {
            let photo = await this.camera.takePictureAsync();
            updateImage(photo.uri);
            /* setSelectedImage({ localUri: photo.uri }); */
        }
        else { alert("Camera isn't ready") }

    }

    if (selectedImage !== null) {
        props.saveImgUrl(selectedImage.localUri);
        return (
            <View style={styles.container}>
                <Image
                    source={{ uri: selectedImage.localUri }}
                    style={styles.smallImage}
                />
            </View>
        );
    }

    if (openCamera !== null) {
        return (
            <View style={styles.container}>
                <Camera ref={ref => { this.camera = ref; }} style={styles.cameraImage} type={type}>
                    <View >
                        <TouchableOpacity
                            style={styles.button}
                            onPress={() => {
                                setType(
                                    type === Camera.Constants.Type.back
                                        ? Camera.Constants.Type.front
                                        : Camera.Constants.Type.back
                                );
                            }}>
                            <Text style={styles.text}> Flip </Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                            onPress={() => { takePicture() }}
                            style={{
                                width: 70,
                                height: 70,
                                top: 330,
                                left: 144,
                                borderRadius: 50,
                                backgroundColor: '#1086f9a1'
                            }}
                        />
                    </View>
                </Camera>
            </View>
        );
    }


    return (
        <View>
            <View >
                <AntDesign
                    style={styles.camera}
                    name="camerao"
                    size={40}
                    color="black"
                    title="Take picture"
                    onPress={() => { startCamera() }}
                />
            </View>
            <View >
                <MaterialCommunityIcons
                    style={styles.gallery}
                    name="image-plus"
                    size={40}
                    color="black"
                    title="Add Picture"
                    onPress={() => { openImagePickerAsync() }}
                />
            </View>
        </View>

    );
}

const styles = StyleSheet.create({

    camera: {
        position: 'absolute',
        left: 66,
        bottom: 34
    },
    gallery: {
        position: 'absolute',
        right: 66,
        bottom: 34
    },
    smallImage: {
        width: 350,
        height: 450,
        left: "5%",
        resizeMode: "contain"
    },

    cameraImage: {
        width: 350,
        height: 450,
        left: "5%",
        resizeMode: "contain"
    },
})
