import React, { useState, useEffect } from 'react'
import { View, StyleSheet, TouchableOpacity } from 'react-native'
import { Text } from 'react-native-paper'
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Button from '../components/Button'
import TextInput from '../components/TextInput'
import BackButton from '../components/BackButton'
import { theme } from '../core/theme'
import { emailValidator } from '../helpers/emailValidator'
import { passwordValidator } from '../helpers/passwordValidator'
import { nameValidator } from '../helpers/nameValidator'
import { useUserContext } from "../src/UserContext"

const RegisterScreen = ({ navigation }) => {
  const [firstName, setFirstName] = useState({ value: '', error: '' })
  const [lastName, setLastName] = useState({ value: '', error: '' })
  const [email, setEmail] = useState({ value: '', error: '' })
  const [password, setPassword] = useState({ value: '', error: '' })
  const [city, setCity] = useState({ value: '', error: '' })
  const [phone, setPhone] = useState({ value: '', error: '' })

  const userContext = useUserContext()
  const {signup} = useUserContext()
  
  const onSignUpPressed = () => {
    const firstNameError = nameValidator(firstName.value)
    const lastNameError = nameValidator(lastName.value)
    const emailError = emailValidator(email.value)
    const passwordError = passwordValidator(password.value)
    const cityError = cityValidator(city.value)
    const phoneError = phoneValidator(phone.value)


    if (emailError || passwordError || firstNameError || lastNameError ) {
      setFirstName({ ...firstName, error: firstnameError })
      setLastName({ ...lastNameError, error: lastNameError })
      setEmail({ ...email, error: emailError })
      setPassword({ ...password, error: passwordError })
      setCity({ ...city, error: cityError })
      setPhone({ ...phone, error: phoneError })
      return
    }
    
    handleSignup();
  }

   const handleSignup = async () => {
     try {
      const ok =  await signup(email.value, password.value);
      console.log("ok: "+ok)
      if (ok) {
         navigation.reset({
          index: 0,
          routes: [{ name: 'LoginScreen' }],
        })  
      }
      
    } catch {
      alert("Failed to create an account")
    }
  };

  /* const authListner = () => {
    fire.auth().onAuthStateChanged(user => {
      if (user) { setUser(user); }
      else { setUser(""); }
    })
  }
  
  useEffect(() => {
    authListner();
  }, []); 
 */

  return (
    <Background>
      <BackButton goBack={navigation.goBack} />
      <Header>Create Account</Header>
      {  <TextInput
        label="First Name"
        returnKeyType="next"
        value={firstName.value}
        onChangeText={(text) => setFirstName({ value: text, error: '' })}
        error={!!firstName.error}
        errorText={firstName.error}
      />}
       {  <TextInput
        label="Last Name"
        returnKeyType="next"
        value={lastName.value}
        onChangeText={(text) => setLastName({ value: text, error: '' })}
        error={!!lastName.error}
        errorText={lastName.error}
      />}
      <TextInput
        label="Email"
        returnKeyType="next"
        value={email.value}
        onChangeText={(text) => setEmail({ value: text, error: '' })}
        error={!!email.error}
        errorText={email.error}
        autoCapitalize="none"
        autoCompleteType="email"
        textContentType="emailAddress"
        keyboardType="email-address"
      />
      {  <TextInput
        label="City"
        returnKeyType="next"
        value={city.value}
        onChangeText={(text) => setCity({ value: text, error: '' })}
        error={!!city.error}
        errorText={city.error}
      />}{  <TextInput
        label="Phone"
        returnKeyType="next"
        value={phone.value}
        onChangeText={(text) => setPhone({ value: text, error: '' })}
        error={!!phone.error}
        errorText={phone.error}
      />}
      <TextInput
        label="Password"
        returnKeyType="done"
        value={password.value}
        onChangeText={(text) => setPassword({ value: text, error: '' })}
        error={!!password.error}
        errorText={password.error}
        secureTextEntry
      />
      <Button
        mode="contained"
        onPress={handleSignup}
        style={{ marginTop: 24 }}
      >
        Sign Up
      </Button>
      <View style={styles.row}>
        <Text>Already have an account? </Text>
        <TouchableOpacity onPress={() => navigation.replace('LoginScreen')}>
          <Text style={styles.link}>Login</Text>
        </TouchableOpacity>
      </View>
    </Background>
  )
}

const styles = StyleSheet.create({
  row: {
    flexDirection: 'row',
    marginTop: 4,
  },
  link: {
    fontWeight: 'bold',
    color: theme.colors.primary,
  },
})

export default RegisterScreen
