import React, { useState } from 'react'
import { Text, View, StyleSheet, Image, ScrollView, SafeAreaView, Animated } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Paragraph from '../components/Paragraph'
import Button from '../components/Button'
import { useUserContext } from "../src/UserContext"
import { SimpleLineIcons, AntDesign, Ionicons, Feather } from '@expo/vector-icons';
import { Divider, Slider } from 'react-native-elements';
import TextInput from '../components/TextInput'
import RestaurantCard from '../components/RestaurantCard'
import OrderCard from '../components/OrderCard'
import SearchSlider from '../components/SearchSlider'


const Dashboard = ({ navigation }) => {
  const Tab = createMaterialBottomTabNavigator();
  const { currentUser, Logout } = useUserContext()


  const restaurants = [
    {
      name: "Abu hasan",
      img: "https://img.haarets.co.il/img/1.7393044/4218340788.jpg",
      city: "Tel Aviv",
      rating: "5.0",
      foodType: "Arab",
      cookTime: "60-80 min"
    },
    {
      name: "Moses",
      img: "https://images.rest.co.il/Customers/80304665/08e182cd2ca347fd96fcb5c889e42eda.jpg",
      city: "Tel Aviv",
      rating: "4.2",
      foodType: "American",
      cookTime: "40-60 min"
    },
    {
      name: "JapanJapan",
      img: "https://www.japan-japan.co.il/warehouse/dynamic/181562.jpg",
      city: "Kfar Saba",
      rating: "4.8",
      foodType: "Japan",
      cookTime: "15-30 min"
    },

  ]

  const orders = [
    {
      name: "Abu hasan",
      date: "24/01/2021",
      price: "50 ILS",
    },
    {
      name: "Japanika",
      date: "28/12/2020",
      price: "185 ILS",
    },
    {
      name: "Rafaelo",
      date: "05/10/2020",
      price: "381 ILS",
    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",

    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",

    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",

    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",

    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",

    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",
    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",
    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",
    },
    {
      name: "Pizza Kimaat Hinam",
      date: "13/08/2020",
      price: "35 ILS",
    },
  ]

  function Search() {
    return (
      <View style={{ alignItems: 'center' }}>
        <View style={{ width: '95%', marginTop: 30 }}>
          <TextInput
            label="Search"
          />
        </View>
        <View style={{ marginBottom: 70, width: '100%' }}>
          <SearchSlider></SearchSlider>
        </View>
        <ScrollView style={{ marginBottom: "30%", width: "100%" }}>
          <View style={{ alignItems: 'center' }}>
            {restaurants.map((item, key) =>
              <RestaurantCard key={key} name={item.name} img={item.img} city={item.city} rating={item.rating} foodType={item.foodType} cookTime={item.cookTime} ></RestaurantCard>
            )}

          </View>
        </ScrollView>
      </View >
    )
  }
  function MyOrders() {
    return (
      <View style={{ marginTop: "10%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text style={{ fontFamily: 'sans-serif-light', color: "#3ec92d", fontSize: 22 }}>My Orders</Text>
        <ScrollView style={{ width: "100%" }}>
          <View style={{ marginTop: "5%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            {orders.map((item, key) =>
              <OrderCard key={key} name={item.name} date={item.date} price={item.price}></OrderCard>
            )}
          </View>
        </ScrollView>
      </View>
    );
  }

  function Profile() {
    return (
      <Background>
        <Button style={{ marginLeft: 280 }}
          onPress={() =>
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
        >
          <Feather name="edit" size={24} color="black" />
        </Button>
        <Image
          source={{ uri: 'https://cdn3.iconfinder.com/data/icons/avatars-round-flat/33/man5-512.png' }}
          style={{ width: 200, height: 200, borderRadius: 200 / 2 }}
        />
        <View style={{ width: '100%', paddingTop: 60, paddingBottom: 60 }}>
          <Text style={styles.profileText} >
            <AntDesign name="user" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.firstName + " " + currentUser.lastName}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="mail" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.email}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="home" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.city}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="phone" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.pNumber}</Text>
        </View>

        <Button
          mode="outlined"
          onPress={() => {
            Logout();
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
          }
        >
          Logout
          </Button>
      </Background>
    );
  }

  async function fetchData() { }




  return (
    <Tab.Navigator
      initialRouteName="Search"
      activeColor="#fff"
      /* barStyle={{ backgroundColor: 'rgb(243,119,0)' }} */
    >

      <Tab.Screen name="Search" component={Search}
        options={{
          tabBarLabel: 'Search',
          tabBarColor: '#009387',
          /* tabBarIcon: ({ color }) => (
            <AntDesign name="search1" size={20} color={ color } />

          ), */
        }}
      />
      <Tab.Screen name="My Orders" component={MyOrders}
        options={{
          tabBarLabel: 'My Orders',
          tabBarColor: '#694fad',
         /*  tabBarIcon: () => (
            <SimpleLineIcons style={styles.tabIcon} name="book-open" size={20} color="white" />
          ), */
        }}
      />
      <Tab.Screen name="Profile" component={Profile}
        options={{
          tabBarLabel: 'Profile',
          tabBarColor: '#694fad',
          /* tabBarIcon: () => (
            <SimpleLineIcons name="user" size={20} color="white" />
          ), */
        }}
      />
    </Tab.Navigator>
  )

}
const styles = StyleSheet.create({
  tabIcon: {
    flex: 0,
    paddingBottom: 10,
  },

  profileText: {
    fontSize: 20,


  },

  profileDivider: {
    backgroundColor: 'rgb(189 ,189 ,189)',
    alignSelf: "center",
    width: 350,
    marginTop: 30,
    marginBottom: 30
  },


})

export default Dashboard
