import React, { useState, useEffect } from 'react'
import { TouchableOpacity, StyleSheet, View } from 'react-native'
import { Text } from 'react-native-paper'
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Button from '../components/Button'
import TextInput from '../components/TextInput'
import BackButton from '../components/BackButton'
import { theme } from '../core/theme'
import { emailValidator } from '../helpers/emailValidator'
import { passwordValidator } from '../helpers/passwordValidator'
import fire from '../fire'
import {emailContext} from '../App'

const LoginScreen = ({ navigation }) => {
  const [user, setUser] = useState({ value: '', error: '' })
  /* const [email, setEmail] = useState({ value: '', error: '' }) */
  const [password, setPassword] = useState({ value: '', error: '' })

  const onLoginPressed = (f) => {
    console.log(f);
    f("bb")

   /*  const emailError = emailValidator(email.value) */
   /*  const passwordError = passwordValidator(password.value) */
   /*  if (emailError || passwordError) {
      setEmail({ ...email, error: emailError })
      setPassword({ ...password, error: passwordError })
      return
    } */
   /*  navigation.reset({
      index: 0,
      routes: [{ name: 'Dashboard' }],
    }) */
/*     handleLogin(); */

  }

  const handleLogin = () =>{
    fire
      .auth()
      .signInWithEmailAndPassword(email,password)
      .catch(err =>{
        switch(err.code){
          case "autu/invalid-email":
          case "autu/user-disabled":
          case "autu/user-not-found":
            setEmailError(err.massege)
            break;
            case "autu/worng-password":
              setPasswordError(err.massege)
            break;
        }

      });
    };

     const handleSingup = () =>{
      fire
        .auth()
        .createUserWithEmailAndPassword(email,password)
        .catch(err =>{
          switch(err.code){
            case "autu/email-alredy-use":
            case "autu/invalid-email":
              setEmailError(err.massege)
              break;
              case "autu/weak-password":
                setPasswordError(err.massege)
              break;
          }

        });
      }; 

      const handleLogout = () =>{
        fire.auth().signOut();
      }

      const authListner = () =>{
        fire.auth().onAuthStateChanged(user =>{
          if(user){setUser(user);}
          else{setUser("");}
        })
      }

      useEffect(() => {
        authListner();
      },[]);



  return (
    <emailContext.Consumer>{
      email=>(
    <Background>
      <BackButton goBack={navigation.goBack} />
      <Logo />
      <Header>Welcome back.</Header>
      <TextInput
        label="Email"
        returnKeyType="next"
        value={email[0]}
        onChangeText={(text) => setEmail({ value: text, error: '' })}
        error={!!email.error}
        errorText={email.error}
        autoCapitalize="none"
        autoCompleteType="email"
        textContentType="emailAddress"
        keyboardType="email-address"
      />
      <TextInput
        label="Password"
        returnKeyType="done"
        value={password.value}
        onChangeText={(text) => setPassword({ value: text, error: '' })}
        error={!!password.error}
        errorText={password.error}
        secureTextEntry
      />
      <View style={styles.forgotPassword}>
        <TouchableOpacity
          onPress={() => navigation.navigate('ForgotPasswordScreen')}
        >
          <Text style={styles.forgot}>Forgot your password?</Text>
        </TouchableOpacity>
      </View>
      <Button mode="contained" onPress={onLoginPressed(email[1])}>
        Login
      </Button>
      <View style={styles.row}>
        <Text>Donâ€™t have an account? </Text>
        <TouchableOpacity onPress={() => navigation.replace('RegisterScreen')}>
          <Text style={styles.link}>Sign up</Text>
        </TouchableOpacity>
      </View>
    </Background>
      )}</emailContext.Consumer>
  )
}

const styles = StyleSheet.create({
  forgotPassword: {
    width: '100%',
    alignItems: 'flex-end',
    marginBottom: 24,
  },
  row: {
    flexDirection: 'row',
    marginTop: 4,
  },
  forgot: {
    fontSize: 13,
    color: theme.colors.secondary,
  },
  link: {
    fontWeight: 'bold',
    color: theme.colors.primary,
  },
})

export default LoginScreen
