import React, { useEffect } from 'react'
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, TouchableOpacity } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useUserContext } from "../src/UserContext"


const CartCard = (props) => {
    const { friendsArrDetails, followUser, unfollowUser } = useUserContext();
    return (
        <Card style={styles.shadow}>
            <View style={styles.user}>
                <Avatar.Image size={48} source={{ uri: props.chefDetails.Img }} />
                <Card.Title title={props.chefDetails.DisplayName} subtitle={'Subtitle'} />
                <View style={{ position: 'absolute', top: '19%', left: '78%' }}>
                    {friendsArrDetails.filter(e => e.email === props.friendData.email).length === 0 ? <TouchableOpacity style={[styles.btn, styles.followButton]} onPress={() => followUser(props.friendData.userUid)} id="btn"><Text style={styles.btnText}>Follow</Text></TouchableOpacity> :
                        <TouchableOpacity style={[styles.btn, styles.unfollowButton]} onPress={() => unfollowUser(props.friendData.uid)} id="btn"><Text style={styles.btnText}>Unfollow</Text></TouchableOpacity>}
                </View>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        flex: 1,
        justifyContent: 'center',
        alignContent: 'center',
        marginBottom: 8,
    },
    user: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
        marginTop: 8,
        marginLeft: 8,
    },
    btn: {
        flex: 1,
        justifyContent: 'flex-end',
        alignItems: 'flex-end',
        color: '#ffffff',
        elevation: 8,
        borderRadius: 10,
        padding: 10,
    },
    followButton: {
        backgroundColor: '#1f9c3a'
    },
    unfollowButton: {
        backgroundColor: '#ba0b1d'
    },
    btnText: {
        color: 'white'
    }

})

export default CartCard;
