import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useUserContext } from "../src/UserContext"


const FriendsReviewsCard = (props) => {
    const userContext = useUserContext()
    const { getDetails, friendsReviews } = useUserContext();
    const userDetails = getDetails(friendsReviews.UserUid)

    /*   email: data.Email,
          firstName: data.FirstName,
              lastName: data.LastName,
                  city: data.City,
                      distance: getDistanceFromLatLonInKm(currentUser.lat, currentUser.lon, data.Lat, data.Lon),
                          img: data.Img */

    return (
        <Card style={styles.shadow} onPress={props.onPress}>
            {/*  <Card.Cover source={{ uri: props.img }} style={{ borderRadius: 15, borderTopRightRadius: 15, borderTopLeftRadius: 15, height: 150, width: 330, }} /> */}
            <Card.Title title={userDetails.firstName + " " + userDetails.lastName} /* subtitle={props.city}  */ />
            <Card.Content>
                <AntDesign name="star" size={20} color="#ffcc00" >
                    <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {friendsReviews.ReviewRating}</Text>
                    {/*  <Text style={{ color: '#dedede' }}> |</Text>
                <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.foodType}</Text>
                <Text style={{ color: '#dedede' }}> |</Text>
                <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.cookTime}</Text> */}
                </AntDesign>
            </Card.Content>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        height: 280,
        width: 370,
        marginTop: 8,
        marginBottom: 8,
        paddingTop: 15,
        alignItems: 'center',
        borderRadius: 15,
    },
})

export default FriendsReviewsCard;

