import React, { useEffect } from 'react'
import PaperButton from '../components/Button'
import { Text, Card } from 'react-native-paper'
import { SimpleLineIcons, AntDesign, Ionicons, Feather, MaterialCommunityIcons, FontAwesome5 } from '@expo/vector-icons';
import BackButton from '../components/BackButton'
import { useUserContext } from "../src/UserContext"
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import FriendCard from '../components/FriendCard'



const FriendsScreen = ({ navigation }) => {
    const { currentChefDishes, currentChef, cart, setOrder, friendsSuggestion, friendsArrDetails } = useUserContext();
    const Tab = createMaterialBottomTabNavigator();

    useEffect(() => {
        if (friendsArrDetails){
            console.log('hi12321312')
            navigation.navigate('FriendsScreen')
        }
    }, [friendsArrDetails])
    function ShowFriends() {
        return (
            <View style={{marginTop: "5%", flex: 1, justifyContent: 'left', alignItems: 'left' }}>
                <View style={{ position: 'absolute', flex: 1, alignItems: 'left' }}>
                    <AntDesign onPress={() => { navigation.reset({ index: 0, routes: [{ name: 'Dashboard' }], }) }} name="arrowleft" size={24} color="black" />
                </View>
                <Text style={{marginBottom: "5%", textAlign: 'center', fontFamily: 'sans-serif-light', color: "#3ec92d", fontSize: 22 }}>My Friends</Text>
                <ScrollView style={{ width: "100%" }}>

                    {friendsArrDetails ? friendsArrDetails.map((item, key) =>
                        <FriendCard key={key}
                            friendData={item}>
                        </FriendCard>
                    ) : <Text>You have no friends yet</Text>}
                </ScrollView>

            </View>
        );
    }
    const myData = [].concat(friendsSuggestion)
        .sort((a, b) => a.score < b.score ? 1 : -1);
    function ShowSuggestions() {
        return (
            <View style={{ marginTop: "5%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                <Text style={{ marginBottom: "5%", textAlign: 'center', fontFamily: 'sans-serif-light', color: "#3ec92d", fontSize: 22 }}>People You May Know</Text>
                <ScrollView style={{ width: "100%" }}>
                    {/* {console.log(myData)} */}
                    {myData ? myData.map((item, key) =>
                        <FriendCard key={key}
                            friendData={item}>
                        </FriendCard>
                    ) : <Text>No suggestion yet</Text>}
                </ScrollView>
            </View>
        );
    }



    return (
        <Tab.Navigator
            initialRouteName="ShowFriends"
            activeColor="#fff"
            barStyle={{ backgroundColor: 'rgb(62,197,0)' }}
        >
            <Tab.Screen name="ShowFriends" component={ShowFriends}
                options={{
                    tabBarLabel: 'Friends',
                    tabBarIcon: ({ color }) => (
                        <SimpleLineIcons name="people" size={20} color={color} />
                    ),
                }}
            />
            <Tab.Screen name="ShowSuggestions" component={ShowSuggestions}
                options={{
                    tabBarLabel: 'Suggestions',
                    tabBarIcon: ({ color }) => (
                        <FontAwesome5 name="people-arrows" size={24} color={color} />
                    ),
                }}
            />
        </Tab.Navigator>
    )
}

export default FriendsScreen

const styles = StyleSheet.create({

    cartBar: {
        height: 100,
        marginTop: '10%',
        width: '100%',
        backgroundColor: 'green',
    },
    shadow: {
        height: 60,
        width: 370,
        marginTop: 8,
        marginBottom: 5,
        elevation: 10,
        borderRadius: 15,
    },
})
