import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, Text } from 'react-native';
import { AntDesign, MaterialCommunityIcons } from '@expo/vector-icons';


const OrderCard = (props) => (
    <Card style={styles.shadow}>
        <Card.Content >
            <View style={{ marginBottom: -30 }}>
                <MaterialCommunityIcons name="silverware-fork-knife" size={24} color="black" >
                    <Text style={{ fontSize: 15, color: 'black' }}> {props.name}</Text>
                    <Text style={{ fontSize: 15, color: '#8c8c8c' }}> - </Text>
                    <Text style={{ fontSize: 15, color: 'black' }}> {props.date}{"\n"}</Text>
                </MaterialCommunityIcons>
            </View>
            <Text style={{ marginTop: 0, textAlign: "right" }}>
                <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 15, color: 'green' }}> {props.price}</Text>
            </Text>
        </Card.Content>
    </Card>
);

const styles = StyleSheet.create({

    shadow: {
        height: 70,
        width: 370,
        marginTop: 8,
        marginBottom: 8,
        elevation: 5,
        borderRadius: 15,
    },
})

export default OrderCard;

