import React from 'react'
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, TouchableOpacity } from 'react-native';



const CartCard = (props) => {
    return (
        <Card style={styles.shadow}>
            <View style={styles.user}>
                {/* <Avatar.Image size={48} source={{ uri: props.chefDetails.Img }} /> */}
                {/* <Card.Title title={props.chefDetails.DisplayName} subtitle={'Subtitle'} /> */}
                <Card.Content>
                    <Text style={{ fontSize: 16, color: 'black' }}> {props.cartDish.Name}</Text>
                    <Text style={{ fontSize: 16, color: '#8c8c8c' }}> - </Text>
                    <Text style={{ fontSize: 16, color: 'black' }}> x {props.cartDish.Quantity} {"\n"}</Text>
                    <Text style={{ marginTop: 0, textAlign: "right" }}>
                        <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 17, color: 'green' }}> {props.cartDish.Price * props.cartDish.Quantity}â‚ª</Text>
                    </Text>
                </Card.Content>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        flex: 1,
        justifyContent: 'center',
        alignContent: 'center',
        marginBottom: 8,
    },
    user: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
        marginTop: 8,
        marginLeft: 8,
    },
})

export default CartCard;
