import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, Text } from 'react-native';
import { AntDesign, MaterialCommunityIcons } from '@expo/vector-icons';


const OrderCard = (props) => (
    <Card style={styles.shadow}>
        <View style={styles.user}>
            <Title>   Order #{props.Order.OrderId} <Text style={{ fontSize: 18 }}>from {props.Order.Chef}</Text></Title>
            <Card.Content style={{ flex: 1, flexDirection: "column" }}>
                <Text style={{ fontSize: 14, color: 'grey' }}> {props.Order.OrderTime}</Text>
                <Text style={{ fontSize: 16, color: 'black' }}> {props.Order.OrderDescription.split("; ").map((item, key) =>
                    <Text>{item ? item + "₪ \n" : null} </Text>)}</Text>
                <View style={{ width: '100%' }}>
                    <Text style={{ fontSize: 16, color: 'black', flexDirection: 'row' }}> Order status: {props.Order.Status == 'close' ? <Text style={{ color: 'green' }}> picked up</Text> : <Text style={{ color: 'orange' }}> waiting for pickup </Text>}
                        <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 17, color: 'green' }}>             {props.Order.TotalPrice}₪</Text>
                    </Text>
                </View>
            </Card.Content>
        </View>
    </Card >

);

const styles = StyleSheet.create({

    shadow: {
        flex: 1,
        width: 370,
        marginTop: 8,
        marginBottom: 5,
        elevation: 10,
        borderRadius: 15,
    },
})

export default OrderCard;
