import * as React from 'react';
import { Text, View, StyleSheet, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Paragraph from '../components/Paragraph'
import Button from '../components/Button'
import { useUserContext } from "../src/UserContext"
import { SimpleLineIcons, AntDesign, Ionicons, Feather } from '@expo/vector-icons';
import { Divider } from 'react-native-elements';
import RestaurantCard from '../components/restaurantCard'



const Dashboard = ({ navigation }) => {

  const Tab = createMaterialBottomTabNavigator();
  const restaurants = [
    {
      name: "Abu hasan",
      img: "url",
      city: "Tel Aviv",
      rating: "5.0",
      foodType: "Arab",
      cookTime: "60-80 min"
    },
    {
      name: "Moses",
      img: "url",
      city: "Tel Aviv",
      rating: "4.2",
      foodType: "American",
      cookTime: "40-60 min"
    },
  ]

  function Search() {
    return (
      restaurants.map((item) =>
        <RestaurantCard key={index}></RestaurantCard>
      )
    )
  }
  function MyOrders() {
    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text>My Orders</Text>
      </View>
    );
  }

  function Profile() {
    return (
      <Background>
        <Button style={{ marginLeft: 280 }}
          onPress={() =>
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
        >
          <Feather name="edit" size={24} color="black" />
        </Button>
        <Image
          source={{ uri: 'https://cdn3.iconfinder.com/data/icons/avatars-round-flat/33/man5-512.png' }}
          style={{ width: 200, height: 200, borderRadius: 200 / 2 }}
        />
        <View style={{ width: '100%', paddingTop: 60, paddingBottom: 60 }}>
          <Text style={styles.profileText} >
            <AntDesign name="user" size={24} color="black" style={{ paddingRight: 20 }} />
          Asaf Sasson</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="mail" size={24} color="black" style={{ paddingRight: 20 }} />
            {currentUser.email}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="home" size={24} color="black" style={{ paddingRight: 20 }} />
           Ruppin</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="phone" size={24} color="black" style={{ paddingRight: 20 }} />
            0525551453</Text>
        </View>

        <Button
          mode="outlined"
          onPress={() =>
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
        >
          Logout
          </Button>
      </Background>
    );
  }

  async function fetchData() { }

  const { currentUser } = useUserContext()


  return (
    <Tab.Navigator
      initialRouteName="Search"
      activeColor="white"
      inactiveColor="#717171"
      barStyle={{ backgroundColor: '#4eb741d6' }}

    >

      <Tab.Screen name="Search" component={Search}
        options={{
          tabBarIcon: () => (
            <AntDesign name="search1" size={20} color="white" />

          ),
        }}
      />
      <Tab.Screen name="My Orders" component={MyOrders}
        options={{
          tabBarIcon: () => (
            <SimpleLineIcons style={styles.tabIcon} name="book-open" size={20} color="white" />
          ),
          style: {
            backgroundColor: '#8e7e7e'
          }
        }}
      />
      <Tab.Screen name="Profile" component={Profile}
        options={{
          tabBarIcon: () => (
            <SimpleLineIcons name="user" size={20} color="white" />
          ),
        }}
      />
    </Tab.Navigator>
  )

}
const styles = StyleSheet.create({
  tabIcon: {
    flex: 0,
    paddingBottom: 10,
  },

  profileText: {
    fontSize: 23,
    fontWeight: 'lighter',
  },

  profileDivider: {
    backgroundColor: 'rgb(189 ,189 ,189)',
    width: '100%',
    margin: '15px'
  },


})

export default Dashboard
