import React, { useState, useEffect } from 'react';
import { View, Text, SafeAreaView, StyleSheet, Animated } from 'react-native';
import Slider from '@react-native-community/slider';
import { useUserContext } from "../src/UserContext"

const SearchSlider = () => {
    const { sliderV, setSliderV } = useState(15)
    const { sliderValue, setSliderValue } = useUserContext();
    useEffect(() => {
        setSliderValue(sliderValue)
    }, [sliderV])
    return (
        <SafeAreaView style={{ flex: 1 }}>
            <View style={styles.container}>
                <Text style={{ color: 'black' }}>Place Radius: {sliderV} Km</Text>
                <Slider
                    maximumValue={30}
                    minimumValue={0.1}
                    minimumTrackTintColor="#307ecc"
                    maximumTrackTintColor="#000000"
                    step={0.1}
                    value={sliderV}
                    onValueChange={(sliderV) => setSliderV(sliderV)}
                />
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        justifyContent: 'center',
        backgroundColor: 'rgb(240,240,240)',
        transform: [{ scaleX: 1 }, { scaleY: 1.3 }]
    },
});

export default SearchSlider;
