import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import NumericInput from 'react-native-numeric-input'


const DishCard = (props) => (
    /*  <Card style={styles.shadow} onPress={props.onPress}>
         <Card.Cover source={{ uri: props.img }} style={{ borderRadius: 15, borderTopRightRadius: 15, borderTopLeftRadius: 15, height: 150, width: 330, }} />
         <Card.Title title={props.name} subtitle={props.description} subtitleNumberOfLines={3} style={{ flex: 1, flexWrap: 'wrap', width: '80%' }} />
         <Card.Content>
             <AntDesign name="star" size={20} color="#ffcc00" >
                 <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.rating}</Text>
                 <Text style={{ color: '#dedede' }}> |</Text>
                 <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.dishType}</Text>
                 <Text style={{ color: '#dedede' }}> |</Text>
                 <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.price}â‚ª</Text>
                 <Text style={{ color: '#dedede' }}> |</Text>
                 <NumericInput totalHeight={30} iconSize={50} value={0} type='up-down' onChange={value => console.log(value)} />
             </AntDesign>
         </Card.Content>
     </Card> */
    <View>
        <NumericInput

            onLimitReached={(isMin, msg) => console.log(isMin, msg)}
            totalWidth={80}
            totalHeight={30}
            iconSize={10}
            step={1}
            minValue={0}
            valueType="real"
            rounded editable={false}
            textColor="#B0228C"
            iconStyle={{ color: "white" }}
            rightButtonBackgroundColor="#18c2ef"
            leftButtonBackgroundColor="#ff8080"
        />
        <NumericInput
            // initValue={this.state.v1}

            totalWidth={150}
            totalHeight={35}
            minValue={0}
            maxValue={9999}
            onLimitReached={(isMAx, msg) => console.log(msg)}
            step={5}
            iconStyle={{ fontSize: 15, color: '#434A5E' }}
            inputStyle={{ fontSize: 18, color: '#434A5E' }}
            valueType='real'
            borderColor='#C7CBD6'
            rightButtonBackgroundColor='#C7CBD6'
            leftButtonBackgroundColor='#C7CBD6'
        />
        <Text style={styles.instructions}>
            Basic Numeric Input - no limits
  </Text>
        <NumericInput
        />
        <View style={styles.seprator} />
        <Text style={styles.instructions}>
            Rounded Numeric Input - with minValue of 0
  </Text>
        <NumericInput
            rounded minValue={0} onLimitReached={(isMax, msg) => console.log(isMax, msg)} />
        <View style={styles.seprator} />

        <Text style={styles.instructions}>
            Basic Numeric Input - with step of 0.5 and valueType real
  </Text>
        <NumericInput
            type='up-down' valueType='real' step={0.5} />
        <View style={styles.seprator} />

        <Text style={styles.instructions}>
            Rounded Numeric Input - with minValue of 0 and maxValue of 5
  </Text>
        <NumericInput rounded type='up-down' minValue={0} validateOnBlur maxValue={5} onLimitReached={(isMax, msg) => console.log(isMax, msg)} />
        <View style={styles.seprator} />

        <Text style={styles.instructions}>
            Rounded Numeric Input - with styling and initial value of 6
  </Text>
        <NumericInput

            rounded type='up-down' textColor='#26547C' iconStyle={{ color: 'green' }} upDownButtonsBackgroundColor='#06D6A0' />
        <View style={styles.seprator} />

        <Text style={styles.instructions}>
            Rounded Numeric Input with styling
  </Text>
        <NumericInput

            rounded textColor='#59656F' iconStyle={{ color: 'white' }} rightButtonBackgroundColor='#AC9FBB' leftButtonBackgroundColor='#DDBDD5' />
        <View style={styles.seprator} />
    </View>



);

const styles = StyleSheet.create({

    shadow: {
        paddingLeft: 20,
        flexWrap: 'wrap',
        flexDirection: 'row',
        height: 280,
        width: 370,
        marginTop: 8,
        marginBottom: 8,
        elevation: 5,
        paddingTop: 15,
        alignItems: 'center',
        borderRadius: 15,
    },
    numeric: {
        height: 32,
        width: 100,
        paddingBottom: 50,
        marginBottom: 50
    }
})

export default DishCard;
