import React, { useContext, useState, useEffect } from 'react'
import { Text, View, StyleSheet, Image, ScrollView, SafeAreaView, Animated } from 'react-native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import Background from '../components/Background'
import Button from '../components/Button'
import { useUserContext } from "../src/UserContext"
import { SimpleLineIcons, AntDesign, Ionicons, Feather, MaterialCommunityIcons } from '@expo/vector-icons';
import { Divider, Slider } from 'react-native-elements';
import TextInput from '../components/TextInput'
import RestaurantCard from '../components/RestaurantCard'
import OrderCard from '../components/OrderCard'
import SearchSlider from '../components/SearchSlider'
import { getDistanceFromLatLonInKm } from '../helpers/calcDis'
import ImagePicker from '../helpers/ImagePicker'
import { degreesToStringHDMS } from 'ol/coordinate';

const Dashboard = ({ navigation }) => {
  const userContext = useUserContext()
  const Tab = createMaterialBottomTabNavigator();
  const { currentUser, Logout, chefsArr, selectedChef, currentChefDishes, selectedImage, sliderValue, orders, getOrders, setCart, getFireStoreImg } = useUserContext();

  useEffect(() => {
    if (currentChefDishes) {
      navigation.navigate('DishesScreen');
    }
  }, [currentChefDishes])

  function Search() {
    return (
      <ScrollView style={{ width: '100%' }}>
        <View style={{ backgroundColor: 'rgb(240, 240, 240)', alignItems: 'center', width: '100%' }}>
          <View style={{ width: '95%', marginTop: 30 }}>
            <TextInput
              label="Search"
            />
          </View>
          <View style={{ width: '100%' }}>
            <SearchSlider></SearchSlider>
          </View>
        </View>
        <View style={{ marginTop: "5%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          {chefsArr.map((item, key) =>
            (sliderValue > item.DisFromUser) ?
              <RestaurantCard name={item.DisplayName} img={item.Img} city={item.Address} rating={item.Rating} foodType={item.FoodType} key={key}
                onPress={async () => {
                  /* handleCardClick(item.Email); */
                  selectedChef(item.Email)
                  setCart([])
                }}>
              </RestaurantCard> : null
          )}
        </View>
      </ScrollView>
    )
  }

  function MyOrders() {
    return (
      <View style={{ marginTop: "10%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text style={{ fontFamily: 'sans-serif-light', color: "#3ec92d", fontSize: 22 }}>My Orders</Text>
        <ScrollView style={{ width: "100%" }}>
          <View style={{ marginTop: "5%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            {orders.map((item, key) =>
              <OrderCard key={key} name={item.Chef} desc={item.OrderDescription} date={item.OrderTime} price={item.TotalPrice}></OrderCard>

            )}
          </View>
        </ScrollView>
      </View>
    );
  }

  function Profile() {
    return (
      <Background>
        <Button style={{ marginLeft: 280 }}
          onPress={() =>
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
        >
        </Button>
        <Image
          source={{ uri: currentUser.img }}
          style={{ width: 200, height: 200, borderRadius: 200 / 2 }}
        />
        <ImagePicker ></ImagePicker>
        <View style={{ width: '100%', paddingTop: 60, paddingBottom: 60 }}>
          <Text style={styles.profileText} >
            <AntDesign name="user" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.firstName + " " + currentUser.lastName}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="mail" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.email}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="home" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.city}</Text>
          <Divider style={StyleSheet.flatten(styles.profileDivider)} />
          <Text style={styles.profileText}>
            <AntDesign name="phone" size={24} color="black" style={{ paddingRight: 20 }} />
            <Text> </Text>{currentUser.pNumber}</Text>
        </View>

        <Button
          mode="outlined"
          onPress={() => {
            Logout();
            navigation.reset({
              index: 0,
              routes: [{ name: 'StartScreen' }],
            })
          }
          }
        >
          Logout
          </Button>
      </Background>
    );
  }

  return (
    <Tab.Navigator
      initialRouteName="Search"
      activeColor="#fff"
      barStyle={{ backgroundColor: 'rgb(243,119,0)' }}
    >

      <Tab.Screen name="Search" component={Search}
        options={{
          tabBarLabel: 'Search',
          tabBarIcon: ({ color }) => (
            <AntDesign name="search1" size={20} color={color} />

          ),
        }}
      />
      <Tab.Screen name="My Orders" component={MyOrders}
        options={{
          tabBarLabel: 'My Orders',
          tabBarIcon: ({ color }) => (
            <SimpleLineIcons style={styles.tabIcon} name="book-open" size={20} color={color} />
          ),
        }}
        listeners={() => ({ tabPress: e => { getOrders() } })}
      />
      <Tab.Screen name="Profile" component={Profile}
        options={{
          tabBarLabel: 'Profile',
          tabBarIcon: ({ color }) => (
            <SimpleLineIcons name="user" size={20} color={color} />
          ),
        }}
        /* listeners={() => ({ tabPress: e => { console.log(currentUser); getFireStoreImg() } })} */
      />
    </Tab.Navigator>
  )
}

const styles = StyleSheet.create({
  tabIcon: {
    flex: 0,
    paddingBottom: 10,
  },
  camera: {
    position: 'absolute',
    left: 66,
    top: 200
  },
  gallery: {
    position: 'absolute',
    right: 66,
    top: 200
  },
  profileText: {
    fontSize: 20,
  },

  profileDivider: {
    backgroundColor: 'rgb(189 ,189 ,189)',
    alignSelf: "center",
    width: 350,
    marginTop: 30,
    marginBottom: 30
  },
})

export default Dashboard




