import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import NumericInput from 'react-native-numeric-input'


const DishCard = (props) => (
    <Card style={styles.shadow} onPress={props.onPress}>
        <Card.Cover source={{ uri: props.img }} style={{ borderRadius: 15, borderTopRightRadius: 15, borderTopLeftRadius: 15, height: 150, width: 330, }} />
        <Card.Title title={props.name} subtitle={props.description} subtitleNumberOfLines={3} style={{ flex: 1, flexWrap: 'wrap', width: '80%' }} />
        <Card.Content>
            <AntDesign name="star" size={20} color="#ffcc00" >
                <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.rating}</Text>
                <Text style={{ color: '#dedede' }}> |</Text>
                <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.dishType}</Text>
                <Text style={{ color: '#dedede' }}> |</Text>
                <Text style={{ fontSize: 15, color: '#8c8c8c' }}> {props.price}â‚ª</Text>
                <Text style={{ color: '#dedede' }}> |</Text>
                <NumericInput style={{paddingBottom: -50}} value={0} type='up-down' onChange={value => console.log(value)} />
            </AntDesign>
        </Card.Content>
    </Card>

    /* <NumericInput 
                value={5} 
                onChange={value => console.log(value)} 
                onLimitReached={(isMax,msg) => console.log(isMax,msg)}
                totalWidth={240} 
                totalHeight={50} 
                iconSize={25}
                step={1.5}
                valueType='real'
                rounded 
                textColor='#B0228C' 
                iconStyle={{ color: 'white' }} 
                rightButtonBackgroundColor='#EA3788' 
                leftButtonBackgroundColor='#E56B70'/> */

);

const styles = StyleSheet.create({

    shadow: {
        paddingLeft: 20,
        flexWrap: 'wrap',
        flexDirection: 'row',
        height: 280,
        width: 370,
        marginTop: 8,
        marginBottom: 8,
        elevation: 5,
        paddingTop: 15,
        alignItems: 'center',
        borderRadius: 15,
    },
    numeric: {
        height: 32,
        width: 100,
        paddingBottom: 50,
        marginBottom: 50
    }
})

export default DishCard;
