import fire from '../fire'
import { auth } from 'firebase'
import React, { useContext, useState, useEffect } from 'react'
import dbFirestore from '../fire'
import { getDistanceFromLatLonInKm } from '../helpers/calcDis'

let apiURLgetUser = "http://192.168.31.156:50247/api/User/getUser/";
let apiURLpostUser = "http://192.168.31.156:50247/api/User/postUser/";
let apiURLgetChefs = "http://192.168.31.156:50247/api/Chef/getChefs/";
let apiURLgetDishes = "http://192.168.31.156:50247/api/Dish/getDishes/";
let apiURLpostOrder = "http://192.168.31.156:50247/api/Order/postOrder/";
let apiURLgetOrder = "http://192.168.31.156:50247/api/Order/getOrder/";

const UserContext = React.createContext()
const UserUpdateContext = React.createContext()



export function useUserContext() {
    return useContext(UserContext)
}

export function useUserUpdateContext() {
    return useContext(UserUpdateContext)
}

export function UserProvider({ children }) {
    const [currentUser, setCurrentUser] = useState('')
    const [currentChef, setCurrentChef] = useState('')
    const [currentChefDishes, setCurrentChefDishes] = useState('')
    const [chefsArr, setChefsArr] = useState('')
    const [name, setName] = useState({ value: '', error: '' })
    const [email, setEmail] = useState({ value: '', error: '' })
    const [password, setPassword] = useState({ value: '', error: '' })
    const [userID, setUserID] = useState('')
    const [selectedImage, setSelectedImage] = useState('');
    const [cart, setCart] = useState([]);
    const [sliderValue, setSliderValue] = useState(26);
    const [orders, setOrders] = useState([]);


    function fireStoreImg() {
        dbFirestore.firestore()
            .collection('userData')
            .doc(currentUser.uid)
            .set({
                imgUrl: currentUser.img
            })
            .then(() => {
                console.log('User added!');
            });
    
    }
    
    function getFireStoreImg() {
        dbFirestore.firestore()
            .collection('userData')
            .doc(currentUser.uid)
            .set({
                imgUrl: currentUser.img
            })
            .then(() => {
                console.log('User added!');
            });
    
    }

    function cartTotal(name, price, quantity) {
        let tempCart;
        let tempDish = {
            Name: name,
            Price: price,
            Quantity: quantity
        }
        let flag = false;
        if (cart) {
            cart.map((item, key) => {
                if (item.Name == name) {
                    item.Quantity = quantity;
                    flag = true;
                    tempCart = cart;
                }
            }
            )
        }
        else {
            setCart([tempDish]);
            return;
        }
        if (!flag) {
            tempCart = cart;
            tempCart.push(tempDish);
        }
        setCart(tempCart);
        console.log(cart);
        console.log(tempDish);
    }

    function getOrders() {
        /* Get Orders from SQL */
        fetch(apiURLgetOrder + currentUser.uid, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json; charset=UTF-8',
                'Accept': 'application/json; charset=UTF-8'
            })
        })
            .then(response => {
                console.log('response.status', response.status);
                return response.json();
            })
            .then((data) => {
                setOrders(data);
                console.log(data);
            },
                (error) => {
                    console.log("err get=", error);
                });

    }

    function setOrder() {
        let sum = 0;
        let orderStr = '';
        cart.forEach(element => {
            if (element.Quantity != 0) {
                sum += (element.Quantity * element.Price)
                orderStr += element.Quantity + 'x ' + element.Name + ' - ' + (element.Quantity * element.Price) + '; ';
            }
        });
        var currentdate = new Date();
        var datetime = currentdate.getDate() + "/"
            + (currentdate.getMonth() + 1) + "/"
            + currentdate.getFullYear() + " - "
            + currentdate.getHours() + ":"
            + currentdate.getMinutes();
        let newOrder = {
            uid: currentUser.uid,
            chefEmail: currentChef.Email,
            orderTime: datetime,
            orderDescription: orderStr,
            totalPrice: sum,
            chef: currentChef.DisplayName
        }
        fetch(apiURLpostOrder, {
            method: 'POST',
            body: JSON.stringify(newOrder),
            headers: new Headers({
                'Content-Type': 'application/json; charset=UTF-8',
                'Accept': 'application/json; charset=UTF-8'
            })
        })
            .then(response => {
                console.log('response=', response);
                console.log('response.status', response.status);
                console.log('response.ok', response.ok);
                return response.json();
            })
        setCart([]);
    }

    function onLogin(email, userID) {
        setEmail(email);
        setUserID(userID);

    }

    function updateImage(img) {
        currentUser.img = img;
    }

    function updateEmail(email) {
        return currentUser.updateEmail(email)
    }

    function updatePassword(password) {
        return currentUser.updatePassword(password)
    }

    async function signup(firstName, lastName, city, phone, email, password, lon, lat) {
        var flag = true;
        await fire
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then(function (user) {
                console.log(user.user.uid + " / " + email + " / " + firstName + " / " + lastName + " / " + city + " / " + phone)
                const newUser = {
                    uid: user.user.uid,
                    email: email,
                    firstName: firstName,
                    lastName: lastName,
                    city: city,
                    pNumber: phone,
                    lon: lon,
                    lat: lat
                }
                console.log(newUser);
                fetch(apiURLpostUser, {
                    method: 'POST',
                    body: JSON.stringify(newUser),
                    headers: new Headers({
                        'Content-Type': 'application/json; charset=UTF-8',
                        'Accept': 'application/json; charset=UTF-8'
                    })
                })
                    .then(response => {
                        console.log('response=', response);
                        console.log('response.status', response.status);
                        console.log('response.ok', response.ok);
                        return response.json();
                    })
            })
            .catch(err => {
                switch (err.code) {
                    case "auth/email-already-in-use":
                    case "auth/invalid-email":
                    case "auth/weak-password":
                        alert(err.message);
                        flag = false;
                        break;
                }
            });


        return flag;
    }

    useEffect(() => {
        getChefs();
    }, [currentUser])



    async function selectedChef(email) {
        await getDishes(email);
        await chefsArr.forEach(element => {
            if (element.Email == email) {
                setCurrentChef(element);
            }
        });
    }

    function getDishes(email) {
        /* Get Dishes from SQL */
        fetch(apiURLgetDishes + email + '/', {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json; charset=UTF-8',
                'Accept': 'application/json; charset=UTF-8'
            })
        })
            .then(response => {
                console.log('response.status', response.status);
                return response.json();
            })
            .then((data) => {
                setCurrentChefDishes(data);
            },
                (error) => {
                    console.log("err get=", error);
                });
    }

    function getChefs() {
        
        /* Get Chefs from SQL */
        fetch(apiURLgetChefs, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json; charset=UTF-8',
                'Accept': 'application/json; charset=UTF-8'
            })
        })
            .then(response => {
                console.log('response.status', response.status);
                return response.json();
            })
            .then((data) => {
                let tempChefs = [];
                data.forEach(element => {
                    const chef = {
                        Address: element.Address,
                        DisplayName: element.DisplayName,
                        Email: element.Email,
                        FirstName: element.FirstName,
                        LastName: element.LastName,
                        PNumber: element.PNumber,
                        Lon: element.Lon,
                        Lat: element.Lat,
                        Rating: element.Rating,
                        FoodType: element.FoodType,
                        Img: element.Img,
                        DisFromUser: getDistanceFromLatLonInKm(currentUser.lat, currentUser.lon, element.Lat, element.Lon)
                    }
                    tempChefs.push(chef);
                });
                setChefsArr(tempChefs);

            },
                (error) => {
                    console.log("err get=", error);
                });
    }

    async function signin(email, password, callbackfunc) {
        let flag;
        await fire.auth()
        .signInWithEmailAndPassword(email, password)
        .then(async () => {
            await fire.auth().onAuthStateChanged(async authUser => {
                if (authUser !== null) {
                    /* Get User Details from SQL */
                    await fetch(apiURLgetUser + authUser.uid, {
                        method: 'GET',
                        headers: new Headers({
                            'Content-Type': 'application/json; charset=UTF-8',
                            'Accept': 'application/json; charset=UTF-8'
                        })
                    })
                    .then(response => {
                        console.log('response.status', response.status);
                                return response.json();
                            })
                            .then(async (data) => {
                                const user = {
                                    uid: data.Uid,
                                    email: data.Email,
                                    firstName: data.FirstName,
                                    lastName: data.LastName,
                                    city: data.City,
                                    pNumber: data.PNumber,
                                    lon: data.Lon,
                                    lat: data.Lat,
                                    img: data.Img
                                }
                                setCurrentUser(user);
                                console.log(user)
                                flag = true;
                                callbackfunc(flag);
                            },
                            (error) => {
                                console.log("err get=", error);
                                
                            })
                            
                    }
                    else {
                        setCurrentUser('');
                    }
                })
            }
            )
            .catch(err => {
                switch (err.code) {
                    case "auth/invalid-email":
                        case "auth/user-disabled":
                            case "auth/user-not-found":
                                alert("wrong Email");
                                console.log(err.message)
                        break;
                        case "auth/wrong-password":
                            alert("wrong Password");
                            console.log(err.message)
                        break;
                    }
                    flag = false;
                    callbackfunc(flag);
                });
        }
        
        function Logout() {
            fire.auth().signOut();
        }
        
        const value = {
        currentUser,
        setCurrentUser,
        name,
        email,
        password,
        userID,
        signup,
        signin,
        updateEmail,
        updatePassword,
        onLogin,
        Logout,
        chefsArr,
        selectedChef,
        currentChefDishes,
        currentChef,
        updateImage,
        cartTotal,
        cart,
        setCart,
        sliderValue,
        setSliderValue,
        setOrder,
        getOrders,
        orders,
        fireStoreImg
    }

    return (
        <UserContext.Provider value={value}>
            {children}
        </UserContext.Provider>
    )
}
