import React, { useEffect, useState } from 'react'
import Button from '../components/Button'
import { Text, Card } from 'react-native-paper'
import { SimpleLineIcons, AntDesign, Ionicons, Feather, MaterialCommunityIcons } from '@expo/vector-icons';
import BackButton from '../components/BackButton'
import DishCard from '../components/DishCard'
import CartCard from '../components/CartCard'
import { useUserContext } from "../src/UserContext"
import { View, StyleSheet, Image, ScrollView, SafeAreaView, Modal, Pressable, Alert } from 'react-native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';





const DishesScreen = ({ navigation }) => {
    const { currentChefDishes, currentChef, cart, setOrder,setCart } = useUserContext();
    const Tab = createMaterialBottomTabNavigator();
    const [modalVisible, setModalVisible] = useState(false);



    function ShowDishes() {
        return (
            <View style={{ height: "100%", width: '100%' }}>
                <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                    <BackButton goBack={navigation.goBack} />
                </View>
                <Text style={{ marginTop: "15%", textAlign: 'center', fontFamily: 'sans-serif-light', color: "#3ec92d", fontSize: 22 }}>{currentChef.DisplayName}'s Menu</Text>
                <ScrollView style={{ width: '100%' }}>
                    <View style={{ marginTop: "5%", flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                        {currentChefDishes.map((item, key) =>
                            <DishCard name={item.DisplayName} img={item.Img}
                                dishType={item.DishType} description={item.Description} price={item.Price} key={key}>
                            </DishCard>
                        )}
                    </View>
                </ScrollView >
            </View>
        );
    }

    const modalPop = () => {
        setModalVisible(true);
        setOrder();
        

    }



    function ShowCart() {
        return (
            <View style={{ height: "100%", alignItems: 'center' }}>
                <Text style={{ marginTop: '10%', fontSize: 35, textAlign: 'center', color: 'green' }}>My Cart</Text>
                {console.log(cart)}
                {(cart.length > 0) ?
                    cart.map((item, key) =>
                        (item.Quantity == 0) ? null : <CartCard currentChef={currentChef} cartDish={item}></CartCard>)
                    :
                    <Text style={{ textAlign: 'center' }}>Your cart is empty</Text>
                }
                {(cart.length > 0) ?
                    <View style={{ marginRight: "2%", marginTop: 'auto', marginLeft: 'auto' }}>
                        <View >
                            <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 17, color: 'green' }}> Cart Total: {cart.reduce((a, b) => a + (b.Price * b.Quantity), 0)}â‚ª</Text>
                        </View>
                        <View>
                            <Button mode="outlined" style={{ borderRadius: 15, backgroundColor: 'rgb(255,138,87)' }} onPress={modalPop}>
                                <Text style={{ color: 'rgb(255 ,255, 255)', fontWeight: 'bold' }}>Order Now</Text>
                            </Button>
                        </View>
                    </View>
                    : null

                }

                <Modal
                    animationType="slide"
                    transparent={true}
                    visible={modalVisible}
                    onRequestClose={() => {
                        Alert.alert("Modal has been closed.");
                        setModalVisible(!modalVisible);
                    }}

                >
                    <View style={styles.centeredView}>
                        <View style={styles.modalView}>
                            <Text style={styles.modalText}>Thank you !</Text>
                            {/* <Text> {cart.map((dish, key) => {dish.Name}) }
                            </Text> */}
                            {console.log(cart)}

                            <Pressable
                                style={[styles.button, styles.buttonClose]}
                                onPress={() => setModalVisible(!modalVisible)}
                            >
                                <Text style={styles.textStyle}>Ok</Text>
                            </Pressable>

                            <Text style={styles.textStyle}>Ok</Text>

                        </View>
                    </View>
                </Modal>


            </View>
        );
    }



    return (
        <Tab.Navigator
            initialRouteName="ShowDishes"
            activeColor="#fff"
            barStyle={{ backgroundColor: 'rgb(62,197,0)' }}
        >
            <Tab.Screen name="ShowDishes" component={ShowDishes}
                options={{
                    tabBarLabel: 'Menu',
                    tabBarIcon: ({ color }) => (
                        <SimpleLineIcons name="book-open" size={20} color={color} />
                    ),
                }}
            />
            <Tab.Screen name="ShowCart" component={ShowCart}
                options={{
                    tabBarLabel: 'Cart',
                    tabBarIcon: ({ color }) => (
                        <AntDesign
                            style={styles.tabIcon}
                            name="shoppingcart"
                            size={20}
                            color={color}
                        />
                    ),
                }}
            />
        </Tab.Navigator>
    )
}


export default DishesScreen

const styles = StyleSheet.create({

    cartBar: {
        height: 100,
        marginTop: '10%',
        width: '100%',
        backgroundColor: 'green',
    },
    shadow: {
        height: 60,
        width: 370,
        marginTop: 8,
        marginBottom: 5,
        elevation: 10,
        borderRadius: 15,
    },

    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
    modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 40,
        padding: 15,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5,
        width: '75%',
        height: '75%'
    },
    button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
    },
    buttonOpen: {
        backgroundColor: "#F194FF",
    },
    buttonClose: {
        backgroundColor: "#2196F3",
    },
    textStyle: {
        color: "white",
        fontWeight: "bold",
        textAlign: "center"
    },
    modalText: {
        marginBottom: 15,
        textAlign: "center"
    }



})
