import React, { useState, useEffect } from 'react';
import { View, Text, SafeAreaView, StyleSheet, Animated } from 'react-native';
import Slider from '@react-native-community/slider';
import { useUserContext } from "../src/UserContext";

const SearchSlider = () => {
    const [sliderValue, setSliderValue] = useState(15);
    const { cSliderValue, setCSliderValue } = useUserContext();
    useEffect(() => {
        setCSliderValue(sliderValue);
    }, [sliderValue])

    return (
        <SafeAreaView style={{ flex: 1 }}>
            <View style={styles.container}>
                <Text style={{ color: 'black' }}>Place Radius: {sliderValue} Km</Text>
                <Slider
                    maximumValue={30}
                    minimumValue={0.1}
                    minimumTrackTintColor="#307ecc"
                    maximumTrackTintColor="#000000"
                    step={0.1}
                    value={sliderValue}
                    onValueChange={(sliderValue) => setSliderValue(sliderValue)}
                />
            </View>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        justifyContent: 'center',
        backgroundColor: 'rgb(240,240,240)',
        transform: [{ scaleX: 1 }, { scaleY: 1.3 }]
    },
});

export default SearchSlider;
