import React from 'react'
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView, TouchableOpacity } from 'react-native';



const CartCard = (props) => {
    return (
        <Card style={styles.shadow}>
            <View style={styles.user}>
                <Avatar.Image variant="square" size={100} source={{ uri: props.cartDish.Img }} />
                {/* <Image source={require('../assets/loadingif.gif')} /> */}
                <Card.Title title={props.chefDetails.DisplayName} />
                <Card.Content>
                    <Text style={{ fontSize: 16, color: 'black' }}> {props.cartDish.Name} - {props.cartDish.Price}</Text>
                    <Text style={{ fontSize: 16, color: 'black' }}> x {props.cartDish.Quantity} {"\n"}</Text>
                    <Text style={{ marginTop: 0, textAlign: "right" }}>
                        <Text style={{ fontFamily: 'sans-serif-medium', fontSize: 17, color: 'green' }}> {props.cartDish.Price * props.cartDish.Quantity}â‚ª</Text>
                    </Text>
                </Card.Content>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow: 'hidden',
        marginBottom: 8,
        height: 130,
        width: 350
    },
    user: {
        flexDirection: 'row',
        marginTop: 8,
        marginLeft: 8,
    },
})

export default CartCard;
