import fire from '../fire'
import { auth } from 'firebase'
import React, { useContext, useState, useEffect } from 'react'
import dbFirestore from '../fire'

let apiURLgetUser = "https://localhost:44338/api/User/getUser/";
let apiURLpostUser = "https://localhost:44338/api/User/postUser/";


const UserContext = React.createContext()
const UserUpdateContext = React.createContext()

export function useUserContext() {
    return useContext(UserContext)
}

export function useUserUpdateContext() {
    return useContext(UserUpdateContext)
}

export function UserProvider({ children }) {
    const [currentUser, setCurrentUser] = useState()
    const [name, setName] = useState({ value: '', error: '' })
    const [email, setEmail] = useState({ value: '', error: '' })
    const [password, setPassword] = useState({ value: '', error: '' })
    const [userID, setUserID] = useState('')

    function onLogin(email, userID) {
        setEmail(email);
        setUserID(userID);
    }

    function updateEmail(email) {
        return currentUser.updateEmail(email)
    }

    function updatePassword(password) {
        return currentUser.updatePassword(password)
    }

    async function signup(firstName, lastName, city, phone, email, password) {
        var flag = true;
        await fire
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then(function (user) {
                console.log("hi1")
                console.log(user.user.uid + " / " + email  + " / " +  firstName + " / " + lastName + " / " + city + " / " + phone)
                const newUser = {
                    uid: user.user.uid,
                    email: email,
                    firstName: firstName,
                    lastName: lastName,
                    city: city,
                    pNumber: phone
                }
                console.log("bye2")
                fetch(apiURLpostUser, {
                    method: 'POST',
                    body: JSON.stringify(newUser),
                    headers: new Headers({
                        'Content-Type': 'application/json; charset=UTF-8',
                        'Accept': 'application/json; charset=UTF-8'
                    })
                })
                    .then(response => {
                        console.log('response=', response);
                        console.log('response.status', response.status);
                        console.log('response.ok', response.ok);
                        return response.json();
                    })
                    .then((result) => {
                        console.log("fetch create new ingredient= ", result);
                        console.log(result.Id + ", " + result.Name + ", " + result.Calories + ", " + result.ImgUrl)
                        this.clearForm();
                    },
                        (error) => {
                            console.log("err post=", error);
                        });
            })
            .catch(err => {
                switch (err.code) {
                    case "auth/email-already-in-use":
                    case "auth/invalid-email":
                    case "auth/weak-password":
                        alert(err.message);
                        flag = false;
                        break;
                }
            });
        if (flag) {
            setDbUser()
        }

        return flag;
    }

    async function setDbUser(/*,,lastName,,city,street,number, */) {
        /* console.log()
        const users = await dbFirestore
             .firestore()
            .collection('userData')
            .doc(currentUser.uid)
            .set({
             
                email: currentUser.email,
            })
            .then(() => {
                console.log('User added!');
            });  */

    }


    function signin(email, password) {
        return fire
            .auth()
            .signInWithEmailAndPassword(email, password)
            .then(() => {
                fire.auth().onAuthStateChanged(authUser => {

                    fetch(apiURLgetUser + authUser.uid, {
                        method: 'GET',
                        headers: new Headers({
                            'Content-Type': 'application/json; charset=UTF-8',
                            'Accept': 'application/json; charset=UTF-8'
                        })
                    })
                        .then(response => {
                            console.log('response=', response);
                            console.log('response.status', response.status);
                            console.log('response.ok', response.ok);
                            return response.json();
                        })
                        .then((data) => {
                            console.log(data)
                            const user = {
                                uid: data.Uid,
                                email: data.Email,
                                firstName: data.FirstName,
                                lastName: data.LastName,
                                city: data.City,
                                pNumber: data.PNumber
                            }
                            setCurrentUser(user)
                            console.log(user)
                        },
                            (error) => {
                                console.log("err get=", error);
                            });
                })

            }
            )
            .catch(err => {
                switch (err.code) {
                    case "auth/invalid-email":
                    case "auth/user-disabled":
                    case "auth/user-not-found":
                        alert("wrong Email");
                        console.log(err.message)
                        break;
                    case "auth/wrong-password":
                        alert("wrong Password");
                        console.log(err.message)
                        break;
                }
            });
    }

    function Logout() {
        fire.auth().signOut();
    }

    /* useEffect(() => {
        const unsubscribe = fire.auth().onAuthStateChanged(user => {
            setCurrentUser(user)
            console.log(user)
        })

        return unsubscribe
    }, []) */

    const value = {
        currentUser,
        name,
        email,
        password,
        userID,
        signup,
        signin,
        updateEmail,
        updatePassword,
        onLogin
    }

    return (
        <UserContext.Provider value={value}>
            {children}
        </UserContext.Provider>
    )
}
