import * as React from 'react';
import { Avatar, Button, Card, Title, Paragraph, Text } from 'react-native-paper';
import { View, StyleSheet, Image, ScrollView, SafeAreaView } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useUserContext } from "../src/UserContext"


const FriendCard = (props) => {
    return (
        <Card style={styles.shadow} onPress={props.onPress}>
            <View style={styles.user}>
                {/*  <Card.Cover source={{ uri: props.img }} style={{ borderRadius: 15, borderTopRightRadius: 15, borderTopLeftRadius: 15, height: 150, width: 330, }} /> */}
                <Avatar.Image size={48} source={{ uri: props.reviewData.UserImg }} />
                <Card.Title title={props.friendsData.firstName + ' ' + props.reviewData.lastName}  />
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({

    shadow: {
        overflow:'hidden',
        alignSelf:'stretch',
        height: 300,
        width: 370,
        marginTop: 8,
        marginBottom: 8,
        paddingTop: 15,
        paddingLeft: 5,
        alignItems: 'baseline'
    },
    user: {
        flexDirection: 'row',
    },
})

export default FriendCard;
